package com.example;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.scheduling.annotation.EnableScheduling;/** * 认证服务启动类 * * @author vains */@EnableScheduling@SpringBootApplicationpublic class AuthorizationExampleApplication {    public static void main(String[] args) {        SpringApplication.run(AuthorizationExampleApplication.class, args);    }}